               PeBL.user.getUser(function(userProfile) {
                 if (userProfile) {
                   Settings.put(userProfile.identity + '_' + ebookURL_filepath, bookmarkString, $.noop);
                 } else {
                   Settings.put(ebookURL_filepath, bookmarkString, $.noop);
                 }
               })
               
