    getAll(index, query, callback) {
        let request = index.openCursor(query);
        let result = [];
        request.onerror = function (e) {
            consoleError("Error", query, e);
        };
        request.onsuccess = function () {
            let r = request.result;
            if (result) {
                if (r) {
                    result.push(r.value);
                    r.continue();
                }
                else if (callback != null)
                    callback(result);
            }
            else {
                if (callback != null) {
                    if (r != null)
                        callback(r.value);
                    else
                        callback([]);
                }
            }
        };
    }
    cleanRecord(r) {
        let recordType = typeof (r);
        if (r && (recordType == "object")) {
            let rec = r;
            for (let p of Object.keys(r)) {
                let v = rec[p];
                let t = typeof (v);
                if (t == "function")
                    delete rec[p];
                else if (t == "array")
                    for (let i = 0; i < v.length; i++)
                        this.cleanRecord(v[i]);
                else if (t == "object")
                    this.cleanRecord(v);
            }
        }
        else if (recordType == "array") {
            let rec = r;
            for (let i = 0; i < rec.length; i++)
                this.cleanRecord(rec[i]);
        }
        return r;
    }
